import React from 'react';
import './App.css';
import Box from '@material-ui/core/Box';
import Toolbar from '@material-ui/core/Toolbar';
import FormControl from '@material-ui/core/FormControl';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import clsx from 'clsx';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import axios from 'axios';
import { Button } from '@material-ui/core';

const styles =
  makeStyles(theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
  dense: {
    marginTop: theme.spacing(2),
  },
  menu: {
    width: 200,
  },
}));

class App extends React.Component {
   constructor(props) {
     super(props);
     this.onClickBtn = this.onClickBtn.bind(this);
     this.textInputArtist = React.createRef();
     this.textInputAlbum = React.createRef();
  }

  onClickBtn() {
    // Accessing the ref using this.refs.inputField
    const artist = this.refs.textInputArtist.value;
    const album = this.refs.textInputAlbum.value;

    if(artist !== '')
    {
        axios.get('https://itunes.apple.com/search?term='+ this.textInputArtist.current.value +'&entity=allArtist&attribute=allArtist')
          .then(response => {
          console.log(response.data);
          })
        .catch(error => {
          console.log(error);
        });
    }
    else if(album !== '')
    {
        axios.get('https://itunes.apple.com/search?term='+ this.textInputAlbum.current.value +'&entity=media&attribute=album')
          .then(response => {
          console.log(response.data);
          })
        .catch(error => {
          console.log(error);
        });
    }
  };


  render() {
    const { classes } = this.props;

    return (
        <React.Fragment>
        <CssBaseline />
        <Container maxWidth="sm">
          <Typography component="div" style={{ backgroundColor: '#cfe8fc', height: '100vh' }}>
          <div style={{ width: '100%' }}>
          <FormControl className={classes.container} noValidate autoComplete="off">
           <Box>
            {"Search by artist:"}
             <TextField
              id="  "
              label="Artist"
              placeholder="Placeholder"
              className={classes.textField}
              margin="normal"
              variant="outlined"
              ref={this.textInputArtist}
            />
            <Button variant="contained" color="primary" className={classes.button} type="submit" onClick={this.onClickBtn}>Search by artist</Button>
            </Box>
            <Box>
              {"Search by album:"}
              <TextField
                id="outlined-with-placeholder"
                label="Album"
                placeholder="Placeholder"
                className={classes.textField}
                margin="normal"
                variant="outlined"
                ref={this.textInputAlbum}
              />
              <Button variant="contained" color="primary" className={classes.button} type="submit" onClick={this.onClickBtn}>Search by album</Button>
            </Box>
            Listar albums o artistas, (1), paginados
            (2)pantalla que muestre todos los albums de un artista
            (3)hacer pantalla que muestre todos los temas del album por album
         </FormControl>
         </div>
         </Typography>
        </Container>
      </React.Fragment>
    );
  }
}

export default withStyles(styles)(App);
