import React, {useEffect, useState} from 'react';
import './App.css';
import Box from '@material-ui/core/Box';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import axios from 'axios';

const useStyles = makeStyles(theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
  dense: {
    marginTop: theme.spacing(2),
  },
  menu: {
    width: 200,
  },
}));


function App() {
  const [values, setValues] = useState({
    album: '',
    artist: '', 
  });

  const classes = useStyles();
  const handleChange = name => event => {
    setValues({ ...values, [name]: event.target.value });
  };

  useEffect(() => {
    if(values.album !== '')
    {
        axios.get('https://itunes.apple.com/search?term='+ values.album +'&entity=media')
          .then(response => {
          console.log(response.data);
          })
        .catch(error => {
          console.log(error);
        });
    }
    else if(values.artist !== '')
    {
        axios.get('https://itunes.apple.com/search?term='+ values.artist +'&entity=media')
          .then(response => {
          console.log(response.data);
          })
        .catch(error => {
          console.log(error);
        });
    }
  },[values]);

  return (
      <React.Fragment>
      <CssBaseline />
      <Container maxWidth="sm">
        <Typography component="div" style={{ backgroundColor: '#cfe8fc', height: '100vh' }} />
        <div style={{ width: '100%' }}>
        <Box display="flex" p={1} bgColor="background.paper">
          {"Búsqueda por artista:"}
           <TextField
            id="outlined-with-placeholder"
            label="Artista"
            placeholder="Placeholder"
            className={classes.textField}
            margin="normal"
            variant="outlined"
            onChange={handleChange('artista')}
          />

           {"Búsqueda por album:"}
           <TextField
            id="outlined-with-placeholder"
            label="Album"
            placeholder="Placeholder"
            className={classes.textField}
            margin="normal"
            variant="outlined"
            onChange={handleChange('album')}
          />

          Listar albums o artistas, (1), paginados
          (2)pantalla que muestre todos los albums de un artista
          (3)hacer pantalla que muestre todos los temas del album por album
        </Box>
       </div>
      </Container>
    </React.Fragment>
  );
}

export default App;
